
public class EvenorOdd {
	
	
	private void evenodd(int i) {
		// TODO Auto-generated method stub

	
	if(i%2==0) {
		System.out.println("it is even");
		
	}else {
		System.out.println("it is odd");
}

}
	public static void main(String[] args) {
		EvenorOdd ev = new EvenorOdd ();
		ev.evenodd(11);
				
	}
}


